mapper.select:
SELECT userPw
FROM users
WHERE userId = #{userId}

mapper.update:
UPDATE users
SET userPw = #{userPw}
WHERE userId = #{userId}

mapper.select:
SELECT NVL(count(*), 0)
FROM userproduct
WHERE userId = #{userId}

SELECT NVL(TO_CHAR(MIN(EXPECTEDENDDATE), 'YYYY-MM-DD'), '없음') AS lastDate
FROM USERPRODUCT
WHERE USERID = #{userId}

SELECT sum(balance) from useraccount a
join account b on a.accountno=b.accountno
where a.userid=#{userNo}
